import * as React from "react";

class Room extends React.Component {

    state = {videoSrc : ""};

    componentDidMount() {

        navigator.mediaDevices.getDisplayMedia({video : true}, ).then((stream) => {
            this.setState({ videoSrc: stream})
        }).catch((error) => {
            console.log(error)
        })
    }

    render() {
        return(
            <div>
                <video src={this.state.videoSrc} autoPlay={true} />
            </div>
        )
    }
}

export default Room
